import React, { Component } from 'react'
import './App.css'

export class App extends Component {
  state = {
    headerTabs: ['TAB 1', 'TAB 2', 'TAB 3', 'TAB 4', 'TAB 5', 'TAB 6', 'TAB 7', 'TAB 8'],
    selectedHeaderTab: 'TAB 1'
  }

  handleHeaderTabs(value) {
    this.setState({ selectedHeaderTab: value })
  }
  render() {
    return (
      <div>
        <ul className='header-tabs'>
                {this.state.headerTabs.map((tab, tabI) => (
                  <li className='tab-item' key={tabI}>
                    <a href={'#'+this.state.selectedHeaderTab}>
                      <button
                      className={
                        tab === this.state.selectedHeaderTab
                          ? 'tab active'
                          : 'tab'
                      }
                      onClick={() => {
                        this.handleHeaderTabs(tab)
                      }}
                    >
                      {tab}
                    </button>
                  </a>
                  </li>
                ))}
              </ul>
      </div>
    )
  }
}

export default App
